import React, { useState } from "react";
import { Grid } from "@material-ui/core";
import { Close as CloseIcon } from "@material-ui/icons";
import { ToastContainer, toast } from "react-toastify";
import SyntaxHighlighter from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";
import classnames from "classnames";
import {Paper, Box} from "@mui/material"

// styles
import "react-toastify/dist/ReactToastify.css";
import useStyles from "./styles";

// components
import Widget from "../../components/Widget/Widget";
import PageTitle from "../../components/PageTitle/PageTitle";
import Notification from "../../components/Notification";
import { Typography, Button } from "../../components/Wrappers/Wrappers";
import test1 from "./test1.png"

const positions = [
  toast.POSITION.TOP_LEFT,
  toast.POSITION.TOP_CENTER,
  toast.POSITION.TOP_RIGHT,
  toast.POSITION.BOTTOM_LEFT,
  toast.POSITION.BOTTOM_CENTER,
  toast.POSITION.BOTTOM_RIGHT,
];

export default function NotificationsPage(props) {
  var classes = useStyles();

  // local
  var [notificationsPosition, setNotificationPosition] = useState(2);
  var [errorToastId, setErrorToastId] = useState(null);

  return (
    <>
      <PageTitle title="POST hahaha" />
      <Grid container spacing={4}>
        <ToastContainer
          className={classes.toastsContainer}
          closeButton={
            <CloseButton className={classes.notificationCloseButton} />
          }
          closeOnClick={false}
          progressClassName={classes.notificationProgress}
        />
        <Grid item xs={12}>
          <Paper elevation={15} 
                 style = {{backgroundColor:"white", height :550, display:"flex", flexDirection:"column"}}
          >
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 20%)"}}>
              <Typography variant="h5">#Classification</Typography>
            </Box>
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 120%)"}}>
              <Typography variant="h5">Author</Typography>
            </Box>
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 240%)"}}>
              <Typography variant="h5">2022 11 6</Typography>
            </Box>
            <Box p={5} style = {{backgroundColor:"gray", position:"absolute",  transform: "translate(0%, 0%)", width:900, height:380}}>
              <img src= {test1} width="100%" border= '2px solid black'></img>
            </Box>
            <Box p={5} style = {{backgroundColor:"gray", position:"absolute",  transform: "translate(0%, 333%)", width:900, height:120}}>
              <div variant="h3" >一輛失控的列車在鐵軌上行駛。在列車正行進的軌道上，有五個人被綁起來，無法動彈。列車將要碾壓過他們。你站在改變列車軌道的操縱杆旁。如果拉動此杆，則列車將切換到另一條軌道上。但是，另一條軌道上也有一個人被綁著。你有兩種選擇</div>
            </Box>
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 480%)"}}>
              <Typography variant="h5">Agree</Typography>
            </Box>
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 600%)"}}>
              <Typography variant="h5">Disagree</Typography>
            </Box>
            <Box p={1} style = {{backgroundColor:"gray", height:50, width:200,position:"absolute", transform: "translate(470%, 720%)"}}>
              <Typography variant="h5">No opinion</Typography>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6} lg={4}>
          <Widget title="Notification Types Examples" disableWidgetMenu>
            <Notification
              className={classes.notificationItem}
              shadowless
              type="message"
              message="Thanks for Checking out Messenger"
              variant="contained"
              color="secondary"
            />
            <Notification
              className={classes.notificationItem}
              shadowless
              type="feedback"
              message="New user feedback received"
              variant="contained"
              color="primary"
            />
            <Notification
              className={classes.notificationItem}
              shadowless
              type="customer"
              message="New customer is registered"
              variant="contained"
              color="success"
            />
            <Notification
              className={classes.notificationItem}
              shadowless
              type="shipped"
              message="The order was shipped"
              variant="contained"
              color="warning"
            />
            <Notification
              className={classes.notificationItem}
              shadowless
              type="delivered"
              message="The order was delivered"
              variant="contained"
              color="primary"
            />
            <Notification
              className={classes.notificationItem}
              shadowless
              type="defence"
              message="5 Defence alerts"
              variant="contained"
              color="info"
            />
          </Widget>
        </Grid>
        <Grid item xs={12} md={6} lg={4}>
          <Widget title="Notification Types Examples" disableWidgetMenu>
            <Notification
              className={classes.notificationItem}
              type="report"
              message="New report has been received"
              color="secondary"
            />
            <Notification
              className={classes.notificationItem}
              type="feedback"
              message="New user feedback received"
              color="primary"
            />
            <Notification
              className={classes.notificationItem}
              type="shipped"
              message="The item was shipped"
              color="success"
            />
            <Notification
              className={classes.notificationItem}
              type="message"
              message="The new message from user @nahawaii"
              color="warning"
            />
            <Notification
