import React, { useState, useEffect } from "react";
import { Grid } from "@material-ui/core";
import { Close as CloseIcon } from "@material-ui/icons";
import { ToastContainer, toast } from "react-toastify";
import SyntaxHighlighter from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";
import classnames from "classnames";
import {Paper, Box, TextField} from "@mui/material"

// styles
import "react-toastify/dist/ReactToastify.css";
import useStyles from "./styles";

// components
import Widget from "../../components/Widget/Widget";
import PageTitle from "../../components/PageTitle/PageTitle";
import Notification from "../../components/Notification";
import { Typography, Button } from "../../components/Wrappers/Wrappers";
import test1 from "./test1.png"

const positions = [
  toast.POSITION.TOP_LEFT,
  toast.POSITION.TOP_CENTER,
  toast.POSITION.TOP_RIGHT,
  toast.POSITION.BOTTOM_LEFT,
  toast.POSITION.BOTTOM_CENTER,
  toast.POSITION.BOTTOM_RIGHT,
];

export default function NotificationsPage(props) {
  var classes = useStyles();

  // local
  var [notificationsPosition, setNotificationPosition] = useState(2);
  var [errorToastId, setErrorToastId] = useState(null);
  const [anyButtonClick, setAnyButtonClick] = useState(false); //if agree/disagree/opinion clicked, show commentBox
  const [AgreeState, setAgreeState] = useState(0); //0: not chosen, 1: agree, 2: disagree, 3: no opinion, 4: A -> D, 5: D-> A
  const [CommentBoxColor, setCommentBoxColor] = useState('');

  useEffect(() => {
    switch(AgreeState){
      case 0:
        setCommentBoxColor('white')
        break;
      case 1: //agree 
        setCommentBoxColor('#3cd4a1')
        break;
      case 2: //disagree
        setCommentBoxColor('#fdaec9')
        break;
      case 3:
        setCommentBoxColor('#bdbdbd')
        break;
      case 4:
        setCommentBoxColor('#fcccdd')
        break;
      case 5:
        setCommentBoxColor('#9aecd0')
        break;

      default:
        setCommentBoxColor('white')

    }
  }, [AgreeState]);

  return (
    <>
      <PageTitle title="POST" />
      <Grid container spacing={4}>
        <ToastContainer
          className={classes.toastsContainer}
          closeButton={
            <CloseButton className={classes.notificationCloseButton} />
          }
          closeOnClick={false}
          progressClassName={classes.notificationProgress}
        />
        <Grid item xs={12}>
          <Paper elevation={15} 
                 style = {{ borderRadius: 7, backgroundColor:"#f5f5f5", height :550, display:"flex", flexDirection:"column"}}
          >
            <Box p={1} style = {{borderRadius: 7,backgroundColor:"#b3e5fc", height:50, width:200,position:"absolute", transform: "translate(470%, 20%)"}}>
              <Typography variant="h5">#Classification</Typography>
            </Box>
            <Box p={1} style = {{borderRadius: 7,backgroundColor:"#b3e5fc", height:50, width:200,position:"absolute", transform: "translate(470%, 140%)"}}>
              <Typography variant="h5">Author</Typography>
            </Box>
            <Box p={1} style = {{borderRadius: 7,backgroundColor:"#b3e5fc", height:50, width:200,position:"absolute", transform: "translate(470%, 260%)"}}>
              <Typography variant="h5">2022 11 6</Typography>
              <Chip label="Chip Outlined" variant="outlined" ></Chip>
            </Box>
            <Box p={5} style = {{backgroundColor:"#f5f5f5", position:"absolute",  transform: "translate(0%, 0%)", width:900, height:380}}>
              <img src= {test1} width="100%" border= '2px solid black'></img>
            </Box>
            <Box p={5} style = {{backgroundColor:"#f5f5f5", position:"absolute",  transform: "translate(0%, 333%)", width:900, height:120}}>
              <div variant="h3" >一輛失控的列車在鐵軌上行駛。在列車正行進的軌道上，有五個人被綁起來，無法動彈。列車將要碾壓過他們。你站在改變列車軌道的操縱杆旁。如果拉動此杆，則列車將切換到另一條軌道上。但是，另一條軌道上也有一個人被綁著。你有兩種選擇</div>
            </Box>
              <Button onClick={()=> setAgreeState(1)} style={{ backgroundColor:'#3cd4a1',height:50, width:200,position:"absolute", transform: "translate(470%, 740%)"}}>Agree</Button>
              <Button onClick={()=> setAgreeState(2)} style={{backgroundColor:'#fdaec9', height:50, width:200,position:"absolute", transform: "translate(470%, 860%)"}}>Disagree</Button>
              <Button onClick={()=> setAgreeState(3)} style={{backgroundColor:'#bdbdbd', height:50, width:200,position:"absolute", transform: "translate(470%, 980%)"}}>No opinion</Button>
          </Paper>
        </Grid>

        <Box style={{ width:"100%", border:10  }} >
          <TextField  fullWidth id="outlined-basic" label="Comment" variant="outlined" sx={{
  "& .MuiOutlinedInput-root": {
    "&.Mui-focused fieldset": {
      borderColor: CommentBoxColor,
      borderWidth: 5
    }
  }
}}/>

        </Box>

        <Grid item xs={12} md={6} lg={4}>
          <Widget title="Agree" disableWidgetMenu>
            <Notification
              className={classes.notificationItem}
              shadowless
              type="message"
              message="Thanks for Checking out Messenger"
              variant="contained"
              color="secondary"
              // color="primary"
              // color="success"
              // color="warning"
              // color="info"
            />
          </Widget>
        </Grid>
        <Grid item xs={12} md={6} lg={4}>
          <Widget title="Disagree" disableWidgetMenu>
            <Notification
              className={classes.notificationItem}
              type="report"
              message="New report has been received"
              color="secondary"
            />
          </Widget>
        </Grid>
        <Grid item xs={12} md={6} lg={4}>
          <Widget title="No opinion" disableWidgetMenu>
            <Notification
              className={classes.notificationItem}
              type="report"
              message="New report has been received"
              variant="rounded"
              color="secondary"
            />
          </Widget>
        </Grid>
      </Grid>
    </>
  );

  // #############################################################
  function sendNotification(componentProps, options) {
    return toast(
      <Notification
        {...componentProps}
        className={classes.notificationComponent}
      />,
      options,
    );
  }

  function retryErrorNotification() {
    var componentProps = {
      type: "message",
      message: "Message was sent successfully!",
      variant: "contained",
      color: "success",
    };
    toast.update(errorToastId, {
      render: <Notification {...componentProps} />,
      type: "success",
    });
    setErrorToastId(null);
  }

  function handleNotificationCall(notificationType) {
    var componentProps;

    if (errorToastId && notificationType === "error") return;

    switch (notificationType) {
      case "info":
        componentProps = {
          type: "feedback",
          message: "New user feedback received",
          variant: "contained",
          color: "primary",
        };
        break;
      case "error":
        componentProps = {
          type: "message",
          message: "Message was not sent!",
          variant: "contained",
          color: "secondary",
          extraButton: "Resend",
          extraButtonClick: retryErrorNotification,
        };
        break;
      default:
        componentProps = {
          type: "shipped",
          message: "The item was shipped",
          variant: "contained",
          color: "success",
        };
    }

    var toastId = sendNotification(componentProps, {
      type: notificationType,
      position: positions[notificationsPosition],
      progressClassName: classes.progress,
      onClose: notificationType === "error" && (() => setErrorToastId(null)),
      className: classes.notification,
    });

    if (notificationType === "error") setErrorToastId(toastId);
  }

  function changeNotificationPosition(positionId) {
    setNotificationPosition(positionId);
  }
}

// #############################################################
function CloseButton({ closeToast, className }) {
  return <CloseIcon className={className} onClick={closeToast} />;
}
