    neutralUsers:[{ type : mongoose.Types.ObjectId, ref: 'User'}],
    a2dUsers:[{ type : mongoose.Types.ObjectId, ref: 'User'}],
    d2aUsers:[{ type : mongoose.Types.ObjectId, ref: 'User'}],


    //comments : used to render the comments to the three boxes
