sourcesContent:
// This file is for initial posts uploaded to db

import mongoose from "mongoose";
import User from "./src/models/user"
import fakeUser from "./user"
// const poster = new User(fakeUser[0]);
// const poster2 = new User(fakeUser[1]);
// const poster3 = new User(fakeUser[2]);
const data = [];
for(var i=0;i<25;i++){
    data.push(
        {
            title: `Title ${i}`,
            hashtag: `#hashtag ${i}`,
            post_time: new Date(1481361366000 + 3600000*i),
            theme: "feeling",
            post_content: `Content ${i}`,
            like: Math.floor(Math.random() * (1000 - 10) + 10),
            dislike: Math.floor(Math.random() * (1000 - 10) + 10),
            commentcount: Math.floor(Math.random() * (1000 - 10) + 10),
            picture: 'data:image/jpeg;base64,'
        })
}

export default data;

